commits.content:
.App {
  text-align: center;
}

.App-logo {
  height: 40vmin;
  pointer-events: none;
}

@media (prefers-reduced-motion: no-preference) {
  .App-logo {
    animation: App-logo-spin infinite 20s linear;
  }
}

.App-header {
  background-color: #282c34;
  min-height: 100vh;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  font-size: calc(10px + 2vmin);
  color: white;
}

.App-link {
  color: #61dafb;
}

@keyframes App-logo-spin {
  from {
    transform: rotate(0deg);
  }
  to {
    transform: rotate(360deg);
  }
}


/* CSS to center the header */
.header-container {
  text-align: center !important;
  margin-top: 20px;
}

.chat {
  /* Add some margin and padding to space out the chat */
  margin: 20px;
  padding: 10px;
}

/* Style for job offer section */
.job-offer {
  margin-top: 20px;
  text-align: center;
}

.input-area {
  margin-top: 10px;
  text-align: center;
}

/* styles.css */
body {
  font-family: Arial, sans-serif;
  margin: 0;
  padding: 0;
  background-color: #f4f4f4;
}

h1 {
  margin-top: 20px;
  color: #333;
  font-size: 2em;
}

.posts {
  display: flex;
  flex-direction: column;
  gap: 20px;
  padding: 20px;
  max-width: 900px;
  margin: 0 auto;
}

.post {
  background-color: #fff;
  border-radius: 8px;
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
  padding: 20px;
  max-width: 700px;
  margin: 0 auto;
  transition: transform 0.3s ease;
}

.post:hover {
  transform: scale(1.02);
}

.post h3 {
  margin: 0 0 10px;
  font-size: 1.5em;
  color: #333;
}

.post p {
  color: #555;
  font-size: 1.1em;
  margin: 10px 0;
}

.post-image {
  width: 100%;
  height: auto;
  border-radius: 8px;
  margin-top: 15px;
  object-fit: cover;
}

.post-actions {
  display: flex;
  gap: 15px;
  margin-top: 15px;
}

.comment-button, .share-button {
  background-color: #008CBA;
  color: white;
  padding: 8px 16px;
  border: none;
  border-radius: 4px;
  cursor: pointer;
  transition: background-color 0.3s;
}

.comment-button:hover, .share-button:hover {
  background-color: #005f73;
}

.chat-button {
  display: inline-block;
  margin-top: 10px;
  padding: 10px 20px;
  background-color: #4CAF50;
  color: white;
  text-decoration: none;
  border-radius: 4px;
  text-align: center;
}

.chat-button:hover {
  background-color: #45a049;
}

.approve-button{
  margin-bottom: 100px;
}
